    heroSubtitle: { en: "Where the Divine Godavari Blesses All", hi: "जहाँ दिव्य गोदावरी सभी को आशीर्वाद देती है", mr: "जिथे दिव्य गोदावरी सर्वांना आशीर्वाद देते" },
    introSub: { en: "The Sacred Riverfront", hi: "पवित्र नदी तट", mr: "पवित्र नदी तीर" },
