    viewOnMap: { en: "View on Google Maps", hi: "Google Maps पर देखें", mr: "Google Maps वर पहा" },
    ramkundName: { en: "Ram Kund", hi: "रामकुंड", mr: "रामकुंड" },
    ramkundSubtitle: { en: "The Holiest Bathing Ghat", hi: "सबसे पवित्र स्नान घाट", mr: "सर्वात पवित्र स्नान घाट" },
    ramkundDesc: {
      en: "Ram Kund is the most sacred ghat in Nashik, believed to be the exact spot where Lord Rama bathed during his 14-year exile.",
      hi: "रामकुंड नाशिक का सबसे पवित्र घाट है, माना जाता है कि यह वही स्थान है जहाँ भगवान राम ने अपने 14 वर्षों के वनवास के दौरान स्नान किया था।",
      mr: "रामकुंड हा नाशिकमधील सर्वात पवित्र घाट आहे, भगवान रामांनी त्यांच्या १४ वर्षांच्या वनवासात जिथे स्नान केले ते हेच ठिकाण असल्याचे मानले जाते.",
    },
    godavariGhatsName: { en: "Godavari Ghats", hi: "गोदावरी घाट", mr: "गोदावरी घाट" },
